$(document).ready(function(){
	/* refresh    */
	$(this).scrollTop(0);

  	var nice = $("html").niceScroll({
    	cursorcolor: "rgba(0, 0, 0, .3)",
		cursorborder: "0px solid #fff"
	});

	$(".requestList, .followList, .theme_scroll").niceScroll({
		autohidemode: 'leave',
    	cursorcolor: "rgba(0, 0, 0, .3)",
		cursorborder: "0px solid #fff"
	});

	var vars = {
		curve: window.innerWidth > 1024 ? 650 : 600
	};
	anime({
		targets: '.hero__background',
		scaleY: [
			{ value: 0, duration: 0 },
			{ value: 1.0, duration: 1500 }
		]
	});
	anime({
		targets: '.hero__wobble',
		translateY: 0,
		scaleY: [
			{ value: [1.75, 1], duration: 500 }
		],
		d: [
			{ value: 'M0 0 C 250 0, 500 0, 500 0 C 500 250, 500 500, 500 500 C 250 ' + vars.curve + ', 0 500, 0 500 C 0 250, 0 500, 0 0 Z' },
			{ value: 'M0 0 C 250 0, 500 0, 500 0 C 500 250, 500 500, 500 500 C 250 500, 0 500, 0 500 C 0 250, 0 500, 0 0 Z' }
		],
		duration: 1500,
		direction: 'normal',
		loop: false
	});

    /* HEADER ALARM */
    $('#bell').click(function() {
        $('.tooltip.alarm').toggle(100, function(){
            if ($(".tooltip.alarm").is(":visible")){
                $('.tooltip-bg').css("display", "block");
            }else{
                $('.tooltip-bg').css("display", "none");
                $('.alarmArea').animate({
                    left: '0px'
                },100);
                $('.followRequestArea').animate({
                    left: '380px'
                },100);
            }
        });
        // $('body').toggleClass("fix");
    });

	$(".tooltip-bg").click(function(){
		if ($(this).is(":visible")){
        	$('body').removeClass("fix");
			$('.tooltip-bg').css("display", "none");
			$('.alarmArea').animate({
				left: '0px'
			},100);
			$('.followRequestArea').animate({
				left: '380px'
			},100);
			$('.tooltip.alarm').css("display", "none");
		}else{
			//nothing
		}
	});

    $('#actFollow').click(function(){
        $('.alarmArea').animate({
            left: '-380px'
        },100);
        $('.followRequestArea').animate({
            left: '0px'
        },100);
    });
    $('#actBack').click(function(){
        $('.alarmArea').animate({
            left: '0px'
        },100);
        $('.followRequestArea').animate({
            left: '380px'
        },100);
    });

    /* mypage */
    const myChart = $("#character_mypage").delay(4000).donutty({
        min: -100,
        max: 100,
        value: 50,
        radius: 60,
        padding: 0,
        color: "rgba(120, 107, 230, .6)",
        bg: "rgba(230, 236, 243, 1)"
    });

	/*  Mobile Navigation  */
    var delay = (function(){
        var timer = 0;
        return function(callback, ms){
            clearTimeout (timer);
            timer = setTimeout(callback, ms);
        };
    })();

    $(function() {
        var pause = 0;
        $(window).resize(function() {
            delay(function() {
                var width = $(window).width();
				var prjHeight = $(".curr_list.prj > ul > li:nth-child(2)").outerHeight();
				$(".curr_list.prj > ul > li:nth-child(1)").css("height", prjHeight - 4);

				if ( width >= 768 ) {
                    $(".m-nav-bg").css({display: "none"});
                    $(".m-nav").css({right: "-300px"});
                    $("body").removeClass("fix");
                } else if ( width <= 767 ) {
                }
            }, pause);
        });
        $(window).resize();
    });


	$(".m-nav-bg").click(function(){
		$(".m-nav").animate({right: "-300px"}, 0);
		$(".m-nav").animate({right: "-300px"}, 0);
		$(".m-nav").animate({right: "-300px"}, 0);
		$(".m-nav").animate({right: "-300px"}, 0);
		$(this).css({display: "none"});
		// $("body").removeClass("fix");
		$("#character svg").remove();
	});
	$("#btnNav").click(function(){
		$(".m-nav-bg").css({display: "block"});
		$(".m-nav").animate({right: "0px"}, 200);
		// $("body").addClass("fix");
		const myChart = $("#character").delay(5000).donutty({
			min: -100,
			max: 100,
			value: 50,
			radius:60,
			padding: 0,
			color: "rgba(120, 107, 230, .6)",
			bg: "rgba(230, 236, 243, 1)"
		});
	});

	/*  Header scroll down  */
	var didScroll;
	var lastScrollTop = 0;
	var delta = 5;
	var navbarHeight = $('header').outerHeight();

	$(window).scroll(function(event){
	    didScroll = true;
	});

	setInterval(function() {
	    if (didScroll) {
	        hasScrolled();
	        didScroll = false;
	    }
	}, 250);

	function hasScrolled() {
	    var st = $(this).scrollTop();
	    if(Math.abs(lastScrollTop - st) <= delta)
	        return;
	    if (st > lastScrollTop){
	        // Scroll Down
			$('.m-header-wrap').addClass('nav-down');
	        $('body.main header').addClass('nav-down');
			$('body.sub header').addClass('nav-down');
	    } else if (st <= 65){
			// 
			$('.m-header-wrap').removeClass('nav-up nav-down');
			$('body.main header').removeClass('nav-up nav-down');
			$('body.sub header').removeClass('nav-up nav-down');
	    } else {
	        // Scroll Up
	        if(st + $(window).height() < $(document).height()) {
				$('.m-header-wrap').addClass('nav-down');
		        $('body.main header').addClass('nav-down');
			    $('body.sub header').addClass('nav-down');
	        }
	    }
	    lastScrollTop = st;
	}
});
